import { networkActionsCreator } from "../actions_creator";

export const actions = networkActionsCreator('network');

export const request = (fetchOptions = {}, action = '', extra = null, networkSagaConfig = {}) => ({
  type: actions.REQUEST,
  payload: {
    options : fetchOptions,
    action,
    extra,
    networkSagaConfig,
  },
})

// export { actions, request };
